import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EventDetails.css';
import Navbar from '../components/Navbar';

const EventDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [destination, setDestination] = useState(null);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const eventResponse = await axios.get(`http://localhost:8000/api/events/${id}`);
        setEvent(eventResponse.data);

        // Fetch associated destination details
        const destinationResponse = await axios.get(
          `http://localhost:8000/api/destinations/${eventResponse.data.destinationId}`
        );
        setDestination(destinationResponse.data);
      } catch (error) {
        console.error('Error fetching event details:', error);
      }
    };

    fetchEventDetails();
  }, [id]);

  if (!event || !destination) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="event-details-container">
      <Navbar />
      <button onClick={() => navigate(-1)} className="back-button">Back</button>

      <div className="event-content">
        <h1>{event.title}</h1>

        <div className="event-info">
          <div className="info-section">
            <h2>Trip Details</h2>
            <p><strong>Start Date:</strong> {new Date(event.start).toLocaleDateString()}</p>
            <p><strong>End Date:</strong> {new Date(event.end).toLocaleDateString()}</p>
          </div>

          <div className="destination-section">
            <h2>Destination Information</h2>
            <h3>{destination.name}</h3>
            <p>{destination.details}</p>

            <div className="packing-list">
              <h3>Packing List</h3>
              {destination.items.map((item, index) => (
                <div key={index} className="packing-item">
                  <input type="checkbox" id={`item-${index}`} />
                  <label htmlFor={`item-${index}`}>{item}</label>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
