  overflow: hidden;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
}

h2 {
  color: #2c3e50;
  font-size: 2.5rem;
  text-align: center;
  margin-top: 60px;
  margin-bottom: 24px;
  width: 100%;
}

.add-button {
  position: absolute;
  top: 40px;
  right: 20px;
  padding: 12px 24px;
  font-size: 1rem;
  background-color: #4CAF50;
