/* Dashboard.css */


  /* Style for the back button */
.back-button {
    background-color: #4CAF50;
    position: absolute;
    color: white;
    border: none;
    padding: 10px 20px;
    font-size: 1rem;
    cursor: pointer;
    border-radius: 5px;
    margin-bottom: 20px;
  }

  .back-button:hover {
    background-color: #45a049;
  }

  /* Style for the add destination form */
  .add-destination-form {
    margin-bottom: 20px;
  }

  .add-destination-form input {
    padding: 10px;
    font-size: 1rem;
    margin-right: 10px;
    border: 1px solid #ddd;
    border-radius: 5px;
    width: 250px;
  }

  .add-destination-form button {
    padding: 10px 20px;
    font-size: 1rem;
    background-color: #4CAF50;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }

  .add-destination-form button:hover {
    background-color: #45a049;
  }
