import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';  // Import Axios
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [destinations, setDestinations] = useState([]);
  const [newDestination, setNewDestination] = useState("");

  // Fetch destinations from MongoDB
  useEffect(() => {
    axios.get('http://localhost:5000/api/destinations')
      .then((response) => setDestinations(response.data))
      .catch((error) => console.error('There was an error fetching destinations:', error));
  }, []);

  // Handle adding a new destination
  const handleAddDestination = (e) => {
    e.preventDefault();
    if (newDestination.trim()) {
      axios.post('http://localhost:5000/api/destinations', {
        name: newDestination,
        details: "Details for " + newDestination,
      })
        .then((response) => {
          setDestinations([...destinations, response.data]);  // Add new destination to the state
          setNewDestination("");  // Clear the input field
        })
        .catch((error) => console.error('There was an error adding the destination:', error));
    }
  };

  return (
    <div className="dashboard-container">
      <button onClick={() => navigate(-1)} className="back-button">Back</button>

      <h2>Your Destinations</h2>

      <form onSubmit={handleAddDestination} className="add-destination-form">
        <input
          type="text"
          value={newDestination}
          onChange={(e) => setNewDestination(e.target.value)}
          placeholder="Add a new destination"
        />
        <button type="submit">Add</button>
      </form>

      <div className="destination-list">
        {destinations.map((destination) => (
          <div
            key={destination._id}  // Use MongoDB ID as key
            className="destination-card"
            onClick={() => navigate(`/destination/${destination._id}`, { state: destination })}
          >
            <h3>{destination.name}</h3>
            <p>{destination.details}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
